mapper.update:
UPDATE MEMBER
SET MONEY = MONEY - (SELECT PRICE FROM ERRAND WHERE MEMBER_ID = #{member_id})
WHERE member_id = #{member_id}

UPDATE MEMBER
SET MONEY = MONEY + (SELECT PRICE FROM ERRAND WHERE MEMBER_ID = #{member_id}) + 2000
WHERE member_id = #{member_id}

UPDATE MEMBER
SET MONEY = MONEY + 2000
WHERE member_id = #{member_id}

mapper.select:
SELECT MEMBER_ID
FROM APPLY
WHERE ERRAND_ID = #{errand_id}
